import Ledger from './Ledger';

describe('RhdJustification', () => {
  const ledger = '';
  const stuctLedger = new Ledger(ledger);

  it('total_ring', () => {
    expect(stuctLedger.total_ring.toNumber()).toEqual(66625320000000);
  });

  it('total_deposit_ring', () => {
    expect(stuctLedger.total_deposit_ring.toNumber()).toEqual(66424320000000);
  });

  it('active_ring', () => {
    expect(stuctLedger.active_ring.toNumber()).toEqual(66618610000000);
  });

  it('active_deposit_ring', () => {
    expect(stuctLedger.active_deposit_ring.toNumber()).toEqual(66424320000000);
  });

  it('total_kton', () => {
    expect(stuctLedger.total_kton.toNumber()).toEqual(23617536000);
  });

  it('active_kton', () => {
    expect(stuctLedger.active_kton.toNumber()).toEqual(23617536000);
  });

  it('value of deposit_items index 0', () => {
    expect(stuctLedger.deposit_items[0].value.toNumber()).toEqual(26424320000000);
  });

  it('start_time deposit_items index 0', () => {
    expect(stuctLedger.deposit_items[0].start_time.toNumber()).toEqual(1567741770);
  });

  it('value unlocking index 3', () => {
    expect(stuctLedger.unlocking[3].value.value.toString()).toEqual('1000000000');
  });

  it('value unlocking index 3', () => {
    expect(stuctLedger.unlocking[3].is_time_deposit.valueOf()).toEqual(false);
  });
});
